mapper.select:
select id, username, password, salt, lasttime
from tb_userlogin
where username = #{username};

select id, username, password, salt, lasttime from tb_userlogin where lasttime

    between #{left} and #{right}


    < #{right}


    > #{left}

;

select count(username)
from tb_userlogin
where username = #{username};

select id, username, password, salt, lasttime
from tb_userlogin
where username = #{username}
  and password = #{password};

select salt
from tb_userlogin
where username = #{username};

select id, username, password, salt, lasttime
from tb_userlogin
where id = #{id};

select username
from tb_userlogin
where id = #{id};

mapper.update:
update tb_userlogin
set password = #{password},
    salt     = #{salt}
where username = #{username};

update tb_userlogin
set lasttime = #{date}
where username = #{name};

update tb_userlogin
set lasttime = #{date}
where id = #{id}
  and username = #{username}
  and password = #{password}
  and salt = #{salt}
  and lasttime = #{lasttime};

update tb_userlogin
set password = #{password},
    salt     = #{salt}
where id = #{id};

update tb_userlogin
set password = #{password},
    salt     = #{salt}
where id = #{id}
  and username = #{username};

update tb_userinfo
set vip = #{vip}
where id = #{id};
